import ContentReveal from "../ui/ContentReveal";

export default function ContentRevealExample() {
  return (
    <div className="py-24 flex items-center justify-center flex-col gap-y-6 not-prose scale-150">
      <ContentReveal
        firstContent={
          <div className="w-full h-64 bg-gray-200 flex items-center justify-center">
            <span className="text-gray-500">Grayscale Image</span>
          </div>
        }
        secondContent={
          <div className="w-full h-64 bg-gradient-to-r from-blue-400 to-purple-500 flex items-center justify-center">
            <span className="text-white font-bold">Colorful Image</span>
          </div>
        }
        lineColor="bg-yellow-500"
        duration={2}
      />
    </div>
  );
}
